        uint256 _timeCoefficient,
        uint256 _bucketSize,
        uint256 _maxPrice,
        uint256 _minPrice
    )
        public
        pure
        returns (bool)
    {
        return _initialPrice > 0
            && _scalingFactor > 0
            && _timeCoefficient > 0
            && _bucketSize > 0
            && _initialPrice <= _maxPrice
            && _initialPrice >= _minPrice;
    }

    /**
     * @dev Returns the encoded data for the price curve parameters
     *
     * @param _initialPrice        Initial price of the auction
     * @param _scalingFactor       Scaling factor for logarithmic expression
     * @param _timeCoefficient     Scaling factor for logarithmic argument
     * @param _bucketSize          Time elapsed between each bucket
     * @param _isDecreasing        Flag for whether the price is decreasing or increasing
     * @param _maxPrice            Maximum price of the auction
     * @param _minPrice            Minimum price of the auction
     */
    function getEncodedData(
        uint256 _initialPrice,
        uint256 _scalingFactor,
        uint256 _timeCoefficient,
        uint256 _bucketSize,
        bool _isDecreasing,
        uint256 _maxPrice,
        uint256 _minPrice
    )
        external
        pure
        returns (bytes memory data)
    {
        return abi.encode(_initialPrice, _scalingFactor, _timeCoefficient, _bucketSize, _isDecreasing, _maxPrice, _minPrice);
    }

    /**
     * @dev Decodes the parameters from the provided bytes.
     *
     * @param _data                Bytes encoded auction parameters
     * @return initialPrice        Initial price of the auction
     * @return scalingFactor       Scaling factor for logarithmic expression
     * @return timeCoefficient     Scaling factor for logarithmic argument
     * @return bucketSize          Time elapsed between each bucket
     * @return isDecreasing        Flag for whether the price is decreasing or increasing
     * @return maxPrice            Maximum price of the auction
     * @return minPrice            Minimum price of the auction
     */
    function getDecodedData(bytes memory _data)
        public
        pure
        returns (uint256 initialPrice, uint256 scalingFactor, uint256 timeCoefficient, uint256 bucketSize, bool isDecreasing, uint256 maxPrice, uint256 minPrice)
    {
        return abi.decode(_data, (uint256, uint256, uint256, uint256, bool, uint256, uint256));
    }

    function _getBoundaryPrice(bool isDecreasing, uint256 maxPrice, uint256 minPrice) private pure returns (uint256) {
        return isDecreasing ? minPrice : maxPrice;
    }
}

contracts/auction-pricing/ConstantPriceA.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity 0.8.17;

/**
 * @title ConstantPriceAdapter
 * @author Index Coop
 * @notice Price adapter contract for AuctionRebalanceModuleV1 that returns a constant price.
 * The rate of change is zero.
 * Price formula: price = initialPrice
 */
contract ConstantPriceAdapter {
    /**
     * @dev Calculates and returns the constant price.
     *
     * @param _priceAdapterConfigData   Encoded bytes representing the constant price.
     *
     * @return price                    The constant price decoded from _priceAdapterConfigData.
     */
    function getPrice(
        address /* _setToken */,
        address /* _component */,
        uint256 /* _componentQuantity */,
        uint256 /* _timeElapsed */,
        uint256 /* _duration */,
        bytes memory _priceAdapterConfigData
    )
        external
        pure
        returns (uint256 price)
    {
        price = getDecodedData(_priceAdapterConfigData);
        require(price > 0, "ConstantPriceAdapter: Price must be greater than 0");
    }

    /**
     * @notice Returns true if the price adapter configuration data is valid.
     *
     * @param _priceAdapterConfigData   Encoded bytes representing the constant price.
     *
     * @return isValid                  True if the constant price is greater than 0, False otherwise.
     */
    function isPriceAdapterConfigDataValid(
        bytes memory _priceAdapterConfigData
    )
        external
        pure
        returns (bool isValid)
    {
        uint256 price = getDecodedData(_priceAdapterConfigData);
        isValid = price > 0;
    }

    /**
     * @notice Encodes the constant price into bytes.
     *
     * @param _price  The constant price in base units.
     *
     * @return        Encoded bytes representing the constant price.
     */
    function getEncodedData(uint256 _price) external pure returns (bytes memory) {
        return abi.encode(_price);
    }

    /**
     * @dev Decodes the constant price from the provided bytes.
     *
     * @param _data  Encoded bytes representing the constant price.
     *
     * @return       The constant price decoded from bytes in base units.
     */
    function getDecodedData(bytes memory _data) public pure returns (uint256) {
        return abi.decode(_data, (uint256));
    }
}

contracts/oracles/PythOracle.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import "@pythnetwork/pyth-sdk-solidity/IPyth.sol";


contract PythOracle {
    IPyth public pyth;
    bytes32 public priceId;

    constructor(address pythContract, bytes32 _priceId) {
        pyth = IPyth(pythContract);
        priceId = _priceId;
    }

    function read() external view returns (uint256 price){
        PythStructs.Price memory pythPrice = pyth.getPrice(priceId);
        price = (uint(uint64(pythPrice.price)) * (10 ** 18)) / (10 ** uint8(uint32(-1 * pythPrice.expo)));
    }
}

